mapper.select:
select id, category_id, content_title, content_detail, dateline_update, dateline_create, is_recommend, sort_id from nxt_content
and @NxtTenantCondition@
and category_id = #{categoryId}
order by is_recommend desc, sort_id desc limit #{offset}, #{limit}

SELECT id, category_id, content_title, content_detail, dateline_update, dateline_create, is_recommend, sort_id from nxt_content WHERE @NxtTenantCondition@ and category_id IN
#{id}
order by is_recommend desc, sort_id desc limit #{offset}, #{limit}

select
  id, category_id, content_title, content_detail, dateline_update, dateline_create, is_recommend, sort_id
from nxt_content

        and @NxtTenantCondition@

        and id = #{id}


        and category_id = #{categoryId}


        and content_title = #{contentTitle}


        and content_detail = #{contentDetail}


        and dateline_update = #{datelineUpdate}


        and dateline_create = #{datelineCreate}


        and is_recommend = #{isRecommend}


        and sort_id = #{sortId}

