mapper.select:
select
  id, region_name, region_pid, sort_id
from nxt_delivery_region

        and @NxtTenantCondition@

        and id = #{id}


        and region_name = #{regionName}


        and region_pid = #{regionPid}


        and sort_id = #{sortId}


order by sort_id asc

select
    id, region_name, region_pid, sort_id
from nxt_delivery_region
WHERE @NxtTenantCondition@ and id IN

    #{id}

limit #{offset}, #{limit}
