SELECT id, category_id, brand_id, product_name, product_subtitle, product_tags, product_ratings, external_url, deal_quantity_min, deal_quantity_max, free_shipping, delivery_config_id, item_no, with_sku, price, price_discount, inventory_quantity, product_description, dateline_create, dateline_updated, is_recommend, is_hot, is_new, is_selling, sort_id, is_trash, commission_rate, sals_count from nxt_product WHERE @NxtTenantCondition@ and id IN
#{id}
and is_trash = 0 order by is_recommend desc, sort_id desc limit #{offset}, #{limit}

SELECT id, category_id, brand_id, product_name, product_subtitle, product_tags, product_ratings, external_url, deal_quantity_min, deal_quantity_max, free_shipping, delivery_config_id, item_no, with_sku, price, price_discount, inventory_quantity, product_description, dateline_create, dateline_updated, is_recommend, is_hot, is_new, is_selling, sort_id, is_trash, commission_rate, sals_count from nxt_product WHERE @NxtTenantCondition@ and category_id IN
#{id}
and is_trash = 0 order by is_recommend desc, sort_id desc limit #{offset}, #{limit}

SELECT count(*) from nxt_product WHERE @NxtTenantCondition@ and category_id IN
#{id}
and is_trash = 0

select
id, category_id, brand_id, product_name, product_subtitle, product_tags, product_ratings, external_url, deal_quantity_min, deal_quantity_max, free_shipping, delivery_config_id, item_no, with_sku, price, price_discount, inventory_quantity, product_description, dateline_create, dateline_updated, is_recommend, is_hot, is_new, is_selling, sort_id, is_trash, commission_rate, sals_count
from nxt_product

        and @NxtTenantCondition@

        and id = #{id}


        and category_id = #{categoryId}


        and brand_id = #{brandId}


        and product_name = #{productName}


        and product_subtitle = #{productSubtitle}


        and product_tags = #{productTags}


        and product_ratings = #{productRatings}


        and external_url = #{externalUrl}


        and deal_quantity_min = #{dealQuantityMin}


        and deal_quantity_max = #{dealQuantityMax}


        and free_shipping = #{freeShipping}


        and delivery_config_id = #{deliveryConfigId}


        and item_no = #{itemNo}


        and with_sku = #{withSku}


        and price = #{price}


        and price_discount = #{priceDiscount}


        and inventory_quantity = #{inventoryQuantity}


        and product_description = #{productDescription}


        and dateline_create = #{datelineCreate}


        and dateline_updated = #{datelineUpdated}


        and is_recommend = #{isRecommend}


        and is_hot = #{isHot}


        and is_new = #{isNew}


        and is_selling = #{isSelling}


        and sort_id = #{sortId}


        and is_trash = #{isTrash}


        and commission_rate = #{commissionRate}


        and sals_count = #{salsCount}

select id, category_id, brand_id, product_name, product_subtitle, product_tags, product_ratings, external_url, deal_quantity_min, deal_quantity_max, free_shipping, delivery_config_id, item_no, with_sku, price, price_discount, inventory_quantity, product_description, dateline_create, dateline_updated, is_recommend, is_hot, is_new, is_selling, sort_id, is_trash, commission_rate, sals_count from nxt_product
and @NxtTenantCondition@
and category_id = #{categoryId}
and is_trash = 0
order by is_recommend desc, sort_id desc limit #{offset}, #{limit}

