mapper.select:
SELECT
id, product_id, uploadfile_id, sort_id
from nxt_product_picture
WHERE @NxtTenantCondition@ and product_id IN

    #{id}

limit #{offset}, #{limit}

select
  id, product_id, uploadfile_id, sort_id
from nxt_product_picture

        and @NxtTenantCondition@

        and id = #{id}


        and product_id = #{productId}


        and uploadfile_id = #{uploadfileId}


        and sort_id = #{sortId}
