mapper.select:
select
  id, origin_type, user_id, product_id, order_form_id, order_form_product_id, dateline, content, parent_id, is_recommend, is_hidden
from nxt_reviews

        and @NxtTenantCondition@

        and id = #{id}


        and origin_type = #{originType}


        and user_id = #{userId}


        and product_id = #{productId}


        and order_form_id = #{orderFormId}


        and order_form_product_id = #{orderFormProductId}


        and dateline = #{dateline}


        and content = #{content}


        and parent_id = #{parentId}


        and is_recommend = #{isRecommend}


        and is_hidden = #{isHidden}

select id, origin_type, user_id, product_id, order_form_id, order_form_product_id, dateline, content, parent_id, is_recommend, is_hidden from nxt_reviews WHERE @NxtTenantCondition@ and parent_id IN
#{id}
order by dateline asc

select id, origin_type, user_id, product_id, order_form_id, order_form_product_id, dateline, content, parent_id, is_recommend, is_hidden from nxt_reviews
and @NxtTenantCondition@
and product_id = #{productId}
and origin_type = 0 and parent_id = 0
order by dateline desc limit #{offset}, #{limit}
