mapper.select:
select
id, username, password, salt, token, phone, email, avatar_id, gender, level_num, money_balance, status,is_admin,
dateline_create, inviter_user_id, can_invite, invitees_count, invite_code
from nxt_user

        and @NxtTenantCondition@

        and id = #{id}


        and username = #{username}


        and password = #{password}


        and salt = #{salt}


        and token = #{token}


        and phone = #{phone}


        and email = #{email}


        and avatar_id = #{avatarId}


        and gender = #{gender}


        and level_num = #{levelNum}


        and money_balance = #{moneyBalance}


        and status = #{status}


        and is_admin = #{isAdmin}


        and dateline_create = #{datelineCreate}


        and inviter_user_id = #{inviterUserId}


        and can_invite = #{canInvite}


        and invitees_count = #{inviteesCount}


        and invite_code = #{inviteCode}

SELECT
id, username, password, salt, token, phone, email, avatar_id, gender, level_num, money_balance, status, is_admin, dateline_create, inviter_user_id, can_invite, invitees_count, invite_code
from nxt_user
WHERE @NxtTenantCondition@ and id IN

    #{id}

limit #{offset}, #{limit}

select
  id, username, password, salt, token, phone, email, avatar_id, gender, level_num, money_balance, status, is_admin, dateline_create, inviter_user_id, can_invite, invitees_count, invite_code
from nxt_user
where @NxtTenantCondition@ and inviter_user_id = #{inviterUserId}
limit #{offset}, #{limit}

select
  id, username, password, salt, token, phone, email, avatar_id, gender, level_num, money_balance, status, is_admin, dateline_create, inviter_user_id, can_invite, invitees_count, invite_code
from nxt_user

        and @NxtTenantCondition@

        and id = #{userId}


        and username = #{username}


        and level_num = #{levelNum}


        and dateline_create >= #{datelineCreateBegin}


        and dateline_create <= #{datelineCreateEnd}

    and (is_admin is NULL or is_admin = 0)

order by id desc
limit #{offset}, #{limit}

select
count(*)
from nxt_user

        and @NxtTenantCondition@

        and id = #{userId}


        and username = #{username}


        and level_num = #{levelNum}


        and dateline_create >= #{datelineCreateBegin}


        and dateline_create <= #{datelineCreateEnd}

    and (is_admin is NULL or is_admin = 0)
